import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import userProfileIcon from './user.svg'; 
import logoutIcon from './logout.svg';

function ProfilePage() {
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchUserInfo() {
      // Obtenir le token JWT du localStorage
      const token = localStorage.getItem('userToken');
      if (!token) {
        console.error('Token is not available');
        setIsLoading(false);
        return;
      }

      const response = await fetch('http://localhost:8080/userinfo', {
        headers: {
          // Envoyer le token dans l'en-tête Authorization
          Authorization: `Bearer ${token}`
        }
      });

      if (!response.ok) {
        // Gérer les erreurs ici, par exemple afficher un message
        console.error('Erreur lors de la récupération des informations de l\'utilisateur');
        setIsLoading(false);
        return;
      }

      const data = await response.json();
      setUserInfo(data);
      setIsLoading(false);
    }

    fetchUserInfo();
  }, []);

  if (isLoading) {
    return <p>Chargement des informations de l'utilisateur...</p>;
  }

  if (!userInfo) {
    return <p>Impossible d'afficher les informations de l'utilisateur.</p>;
  }

  // Gère la logique de déconnexion ici, par exemple effacer le token du localStorage et rediriger vers la page de connexion
  function handleLogout() {
    localStorage.removeItem('userToken'); // Supprime le token du localStorage
    //navigate('/');
  }

  return (
    <div className="profile-page">
      <header className="profile-header">
        <Link to="/"><img src={userProfileIcon} alt="Profil" /></Link>
        <h1>SPOTTREND QUIZ</h1>
        <Link to="/logout" onClick={handleLogout}><img src={logoutIcon} alt="Déconnexion" /></Link>
      </header>
      <div className="profile-content">
        <h2>{userInfo.Pseudo}</h2>
        <div className="profile-details">
          <p>Nombre de parties : {userInfo.NbDeParties}</p>
          <p>Score total : {userInfo.ScoreTotal}</p>
          <div className="profile-history">
            <h3>Historique des scores</h3>
            <ul>
              {userInfo.ScoreHistory.map((score, index) => (
                <li key={index}>{score}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
