import React, { useState, useEffect } from 'react';
import { useLocation, Link} from 'react-router-dom';
import logo from './logo.svg'; 
import userIcon from './user.svg'; 
import logoutIcon from './logout.svg'; 

function ResultPage() {
  const location = useLocation();
  const { score } = location.state || { score: 'Score non trouvé' };
  const [quizResults, setQuizResults] = useState({
    scoreTotal: 0,
    scoreHistory: [],
    userRanking: []
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchQuizResults = async () => {
      const token = localStorage.getItem('userToken');
      try {
        const response = await fetch('http://localhost:8080/get-result', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des résultats du quiz');
        }
        const data = await response.json();
        setQuizResults(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des résultats du quiz', error);
        setIsLoading(false);
      }
    };

    fetchQuizResults();
  }, []);

  const signOut = () => {
    localStorage.removeItem('userToken'); // Supprime le token du localStorage
    console.log("Déconnexion de l'utilisateur");
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="logo-container">
          <Link to="/"><img src={logo} alt="Logo" className="home-logo" /></Link>
        </div>
        <div className="user-logout-container">
          <Link to="/profile"><img src={userIcon} alt="Profil Utilisateur" className="user-icon" /></Link>
          <img src={logoutIcon} alt="Déconnexion" className="logout-icon" onClick={signOut} style={{cursor: 'pointer'}} />
        </div>
      </div>
      <div className="content">
        <h1>Page de Résultats</h1>
        <p>Votre score pour cette partie est : {score}</p>
        <p>Votre score total est : {quizResults.scoreTotal}</p>
        <h2>Historique des scores</h2>
        {isLoading ? (
          <p>Chargement...</p>
        ) : (
          <ul>
            {quizResults.scoreHistory.map((score, index) => (
              <li key={index}>{score}</li>
            ))}
          </ul>
        )}
        <h2>Classement</h2>
        {isLoading ? (
          <p>Chargement du classement...</p>
        ) : (
          <div>
            {quizResults.userRanking.map((user, index) => (
              <div key={index}>
                <p>{index + 1} - {user.pseudo} avec un score de {user.score}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ResultPage;
