import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import userProfileIcon from './user.svg'; // Assurez-vous que le chemin d'accès est correct
import logoutIcon from './logout.svg'; // Assurez-vous que le chemin d'accès est correct

function ProfilePage() {
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchUserInfo() {
      // Obtenir le token JWT du localStorage
      const token = localStorage.getItem('userToken');
      if (!token) {
        console.error('Token is not available');
        setIsLoading(false);
        navigate('/signin'); // Redirigez l'utilisateur vers la page de connexion si le token n'est pas là
        return;
      }

      try {
        const response = await fetch('http://localhost:8080/userinfo', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user info.');
        }

        const data = await response.json();
        setUserInfo(data);
      } catch (error) {
        console.error('Erreur lors de la récupération des informations de l\'utilisateur', error);
        navigate('/signin'); // Redirigez l'utilisateur vers la page de connexion en cas d'erreur
      } finally {
        setIsLoading(false);
      }
    }

    fetchUserInfo();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('userToken'); // Supprime le token du localStorage
    navigate('/'); // Redirige l'utilisateur vers la page de connexion
  };

  if (isLoading) {
    return <p>Chargement des informations de l'utilisateur...</p>;
  }

  if (!userInfo) {
    return <p>Impossible d'afficher les informations de l'utilisateur.</p>;
  }

  return (
    <div className="profile-page">
      <header className="profile-header">
        <Link to="/"><img src={userProfileIcon} alt="Profil" /></Link>
        <h1>SPOTTREND QUIZ</h1>
        <img src={logoutIcon} alt="Déconnexion" onClick={handleLogout} />
      </header>
      <div className="profile-content">
        <h2>{userInfo.pseudo}</h2> {/* Assurez-vous que les clés correspondent à celles retournées par votre serveur */}
        <p>Nombre de parties : {userInfo.nbDeParties}</p>
        <p>Score total : {userInfo.scoreTotal}</p>
        <h3>Historique des scores</h3>
        <ul>
          {userInfo.scoreHistory && userInfo.scoreHistory.map((score, index) => (
            <li key={index}>{score}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ProfilePage;
