import React, { useState, useRef } from 'react';

function SignInPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    pseudo: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    signIn(formData.pseudo, formData.password)
      .then((data) => {
        // Stocker le token reçu dans localStorage
        localStorage.setItem('userToken', data.token);
        // Indiquer à l'utilisateur que la connexion a été réussie
        alert('Connexion réussie !');
        // Rediriger l'utilisateur vers la page d'accueil
        navigate('/');
      })
      .catch((error) => {
        console.error('Erreur lors de la connexion', error);
        alert(`${error.message}`);
      });
  };

  return (
    <div className="signup-page">
      <h1>Se connecter</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="pseudo">Pseudo :</label>
          <input
            type="text"
            id="pseudo"
            name="pseudo"
            value={formData.pseudo}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Mot de passe :</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
}


function signIn(pseudo, password) {
  return fetch('http://localhost:8080/signin', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({pseudo, password}),
  })
  .then(response => {
    if (!response.ok) {
      // Lire le texte de la réponse pour obtenir le message d'erreur
      return response.text().then(text => Promise.reject(new Error(text || 'Échec de connexion')));
    }
    return response.json(); 
  });
}

export default SignInPage;
