import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import logo from './logo.svg';
import userIcon from './user.svg'; // Idem pour l'icône utilisateur

function HomePage() {
  const [topPlayers, setTopPlayers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchTopPlayers() {
      try {
        const response = await fetch('http://localhost:8000/topPlayers'); // Assure-toi que cet endpoint correspond à ce que ton serveur attend
        if (!response.ok) {
          throw new Error('Impossible de récupérer les meilleurs joueurs.');
        }
        const data = await response.json();
        setTopPlayers(data);
      } catch (error) {
        console.error('Erreur lors de la récupération des meilleurs joueurs:', error);
        // Ici, tu pourrais mettre en place une gestion d'erreur plus robuste
      } finally {
        setIsLoading(false);
      }
    }

    fetchTopPlayers();
  }, []);

  const startQuiz = () => {
    // Remplacer par la logique nécessaire, peut-être une vérification d'état de connexion ou autre
    navigate('/quiz'); // Redirige vers le quiz
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        {/* L'image cliquable pour revenir à la page d'accueil */}
        <Link to="/"><img src={logo} alt="Logo" className="home-logo" /></Link>

        {/* Le bouton ou icône de déconnexion */}
        <button className="logout-button" onClick>Déconnexion</button>

        {/* L'icône cliquable pour aller à la page de profil */}
        <Link to="/profile"><img src={userIcon} alt="User Profile" className="user-icon" /></Link>
      </div>
      <div className="content">
        <h1>SPOTTREND QUIZ</h1>
        <h2>Classement</h2>
        {isLoading ? (
          <p>Chargement des meilleurs joueurs...</p>
        ) : (
          <table className="leaderboard">
            <thead>
              <tr>
                <th>Position</th>
                <th>Pseudo</th>
                <th>Score Total</th>
              </tr>
            </thead>
            <tbody>
              {topPlayers.map((player, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{player.pseudo}</td>
                  <td>{player.scoreTotal}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
        <button onClick={startQuiz}>COMMENCER</button>
      </div>
    </div>
  );
}

export default HomePage;
